<style>	.sort {	    border: 1px solid #ccc;	    list-style: none;	    padding: 10px;	    margin-top: 5px;	    width: 600px    }</style><ul style="margin: 10px 0" id="sortlist"> <?php     $this->load->module('web_pages');    foreach ($blocks->result() as $row) {          $data = $this->web_pages->get_data_from_db($row->page_id);    $page_title = $data['page_title'];    $page_url = $data['page_url'];    ?>	<li id="<?php echo $row->id; ?>" class="sort"><i class="fa fa-sort fa-fw fa-lg"></i>&nbsp; <b><?= $this->lang->line('page_url') ?>: </b><?= $page_url ?> | <b><?= $this->lang->line('page_title') ?>: </b><?= $page_title ?>		<?php		$edit_url = base_url() . "btm_nav/deleteconif/" . $row->id;          ?> &nbsp;          <a href="<?php echo $edit_url?>">          	<button class="btn btn-danger btn-sm">          	<i class="fa fa-trash fa-fw"></i>          	</button>           </a>     </li>	<?php } ?></ul>