<style>	.sort {	    border: 1px solid #ccc;	    list-style: none;	    padding: 10px;	    margin-top: 5px;	    width: 600px    }</style><ul style="margin: 10px 0" id="sortlist"> <?php                 $this->load->module('store_category');                foreach ($categorise->result() as $row) {                  if($row->cat_parent_id == 0) {                    $cat_parent_title = "-";                  } else {                    $cat_parent_title = $this->store_category->_get_cat_parent_name($row->cat_parent_id);                  }                                    $get_count_categories = $this->store_category->_get_count_category($row->id);                                                    ?>				<li id="<?php echo $row->id; ?>" class="sort"><i class="fa fa-sort fa-fw fa-lg"></i>&nbsp;<?php echo $row->cat_name ?>   					<?php					$edit_url = base_url() . "store_category/create/" . $row->id;				                if($get_count_categories == 0) {	                  	                  }else {	                  		                  	$categories_url = base_url() . "store_category/manage/" . $row->id;	                  							if($get_count_categories == 1) {							$entity = "Category";						} else {							$entity = "Categories";						}												echo '<a href="'.$categories_url.'" style="background-color: #ccc" class="badge">'.$get_count_categories . " Sub-" . $entity .'</a>';	                  }	                  ?> &nbsp; <a href="<?php echo $edit_url?>"><i class="fa fa-edit"></i> </a>                  </li>	<?php } ?></ul>